import {
  ChevronRight,
  Menu,
  Package,
  Puzzle,
  TableOfContents,
  Webhook,
  X,
} from "lucide-react";
import { Link, useLocation } from "react-router-dom";
import React, { useState } from "react";

import ThemeSelector from "./ThemeSwitcher";

const navItems = [
  { label: "Overview", path: "/", icon: TableOfContents },
  { label: "Components", path: "/components", icon: Package },
  { label: "Plugins", path: "/plugins", icon: Puzzle },
  { label: "API", path: "/apis", icon: Webhook },
];

const NavLink = ({
  item,
  isActive,
}: {
  item: (typeof navItems)[0];
  isActive: boolean;
}) => {
  const Icon = item.icon;

  return (
    <Link
      to={item.path}
      className={`flex items-center gap-2 px-4 py-2 rounded-lg text-sm font-medium
                 transition-all duration-200 group ${
                   isActive
                     ? "bg-primary/10 text-primary"
                     : "text-muted-foreground hover:text-muted-foreground/80 hover:bg-card"
                 }`}
    >
      <Icon
        size={18}
        className={`${isActive ? "text-primary" : "text-gray-500"} 
                                 transition-colors group-hover:text-inherit`}
      />
      <span>{item.label}</span>
      {isActive && <ChevronRight size={16} className="ml-auto text-primary" />}
    </Link>
  );
};

